Scripts.OnShow:
PlaySound("UChatScrollButton");
PanelTemplates_SetTab(ETP_Frame, 4);
onglet_re:Hide();
onglet_ka:Hide();
onglet_ot:Hide();
onglet_nf:Show();

Scripts.OnMouseDown:
if button == "MiddleButton" and not self.isLocked then
self:StartMoving();
self.isMoving = true;
end

Scripts.OnMouseUp:
if button == "MiddleButton" and self.isMoving then
self:StopMovingOrSizing();
self.isMoving = false;
end

Scripts.OnHide:
if ( this.isMoving ) then
this:StopMovingOrSizing();
this.isMoving = false;
end
